prev_point = msg.payload.rows[1].value; //JSON object containing values related to the previous Car Probe Point
msg.payload = msg.payload.rows[0].value; //JSON object containing values related to the current Car Probe Point

//Find time difference between the two Car Probe Point (in s). It will be used for calculating some of the values
t_diff = msg.payload.t_diff = (Math.abs(new Date(msg.payload.timestamp) - new Date(prev_point.timestamp)))/1000;

//Add the following to the data to that retrieved from the Cloudant NoSQL DB
//Set values for heading, longitude, latitude, velocity vectors,resultant speed and distance travelled
msg.payload.V_x = prev_point.V_x + msg.payload.acc_x*t_diff; // Velocity in the x direction (in m/s)
msg.payload.V_y = prev_point.V_y + msg.payload.acc_y*t_diff; // Velocity in the y direction (in m/s)
msg.payload.V_z = prev_point.V_z + msg.payload.acc_z*t_diff; // Velocity in the z direction (in m/s)

// Calculate Resultant speed (in m/s)
msg.payload.Sr = Math.sqrt( Math.pow(msg.payload.V_x, 2) + Math.pow(msg.payload.V_y, 2) + Math.pow(msg.payload.V_z, 2) );

//Calculate Distance travelled (in m)
msg.payload.distance = msg.payload.Sr * t_diff;
global.set("distance", global.get("distance") + msg.payload.distance); // update total distance (in m)

if (msg.payload.distance !== 0){
    //Calculate new heading //Angle of moving direction, North is 0 and value increases clockwise (in degrees)
	msg.payload.heading = Math.acos(msg.payload.V_y/msg.payload.distance)*180/Math.PI;

	//Longitude and Latitude of previous Car Probe Point (in degrees)
	lat1 = prev_point.latitude;
	lon1 = prev_point.longitude;

	var radius = 6371e6; // (Mean) radius of earth in meters
	var toRadians = function(v) { return v * Math.PI / 180; };
	var toDegrees = function(v) { return v * 180 / Math.PI; };

	var angular_dist = msg.payload.distance / radius; // angular distance in radians
	var heading_rad = toRadians(msg.payload.heading);

	var lat1_rad = toRadians(lat1);
	var lon1_rad = toRadians(lon1);

	var sin_lat1_rad = Math.sin(lat1_rad), cos_lat1_rad = Math.cos(lat1_rad);
	var sin_angular_dist = Math.sin(angular_dist), cos_angular_dist = Math.cos(angular_dist);
	var sin_heading_rad = Math.sin(heading_rad), cos_heading_rad = Math.cos(heading_rad);

	var sin_lat2_rad = sin_lat1_rad*cos_angular_dist + cos_lat1_rad*sin_angular_dist*cos_heading_rad;
	var lat2_rad = Math.asin(sin_lat2_rad);
	var y = sin_heading_rad * sin_angular_dist * cos_lat1_rad;
	var x = cos_angular_dist - sin_lat1_rad * sin_lat2_rad;
	var lon2_rad = lon1_rad + Math.atan2(y, x);

	var lat2 = toDegrees(lat2_rad);
	var lon2 = (toDegrees(lon2_rad)+540)%360-180; // normalise to âˆ’180..+180 degrees

	msg.payload.latitude = lat2;
	msg.payload.longitude = lon2;

}else{
    //If no deistance is travelled use the heading, longitude and latitude of the previous Car Probe Point
	msg.payload.heading = prev_point.heading;
	msg.payload.latitude = prev_point.latitude;
	msg.payload.longitude = prev_point.longitude;
}

//Url to be passed to update the doc to which the above data should belong to
