var prev_Sr = msg.payload.rows[1].value.Sr; // JSON object containing values related to the previous Car Probe Point
var curr_Sr = msg.payload.rows[0].value.Sr; // JSON object containing values related to the current Car Probe Point
var thres_Sr_diff = 4; // Threshold to determine harsh acceleration and braking
var thres_Sr = 15; // Threshold to determine overspeeding

// Variables used to determine if acceleration, braking and stopping are happening frequently
var stop = 0;
var freq_acc = 0;
var freq_brake = 0;

var thres_count = 3; // Threshold of when to consider calculating frequent acceleration, braking and stopping

//Set Harsh Acceleration //recognized if the acceleration data exceeds a threshold value
msg.payload.rows[0].value.harsh_acc = (curr_Sr - prev_Sr) > thres_Sr_diff;
if(msg.payload.rows[0].value.harsh_acc === true){ //set frequency at which Harsh Acceleration happens
    global.set("harsh_acc_count", global.get("harsh_acc_count") + 1);
}

//Set Harsh Braking //recognized if the deceleration exceeds the threshold value
msg.payload.rows[0].value.harsh_brake = (curr_Sr - prev_Sr) < -thres_Sr_diff;
if(msg.payload.rows[0].value.harsh_brake === true){ //set frequency at which Harsh Braking happens
    global.set("harsh_brake_count", global.get("harsh_brake_count") + 1);
}

//Set Overspeeding //Based on a threshold value
msg.payload.rows[0].value.overspeed = curr_Sr > thres_Sr; 
if(msg.payload.rows[0].value.overspeed === true){ //set frequency at which Overspeeding happens
    global.set("overspeed_count", global.get("overspeed_count") + 1);
}

//Calculate frequent acceleration, braking and stopping
if(msg.payload.total_rows > 5){
	for (var i in msg.payload.rows){
		if(msg.payload.rows[i].value.Sr === 0){
			stop++;
		}

		if(msg.payload.rows[i].value.harsh_acc === true){
			freq_acc++;
		}

		if(msg.payload.rows[i].value.harsh_brake === true){
			freq_brake++;
		}
	}
}

//Set Frequent stopping //Frequent stops are recognized if there are many stop instances within a certain range of Car Probe Points
msg.payload.rows[0].value.freq_stop = stop > thres_count; 
